import React, { Component } from 'react'

class AddComponent extends Component {

 constructor(props) {
        super(props);
        this.onChangeSource = this.onChangeSource.bind(this);
        this.onChangeDestination = this.onChangeDestination.bind(this);
        this.onChangedate = this.onChangedate.bind(this);
        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeFare = this.onChangeFare.bind(this);
        this.onChangeBoardingTime= this.onChangeBoardingTime.bind(this);
        this.onChangeArrivalTime= this.onChangeArrivalTime.bind(this);
        
        this.saveFlight = this.saveFlight.bind(this);
        this.newFlight = this.newFlight.bind(this);
    
        this.state = {
            source: "",
            destination: "",
            date: "", 
            name: "",
            fare:"",
            boardingtime:"",
            arrivaltime:"",
      
            submitted: false
          };}

          onChangeSource(e) {
            this.setState({
              source: e.target.value
            });
          }
          onChangeDestination(e) {
            this.setState({
              destination: e.target.value
            });
          }
          onChangedate(e) {
            this.setState({
              date: e.target.value
            });
          }
          onChangeName(e) {
            this.setState({
              name: e.target.value
            });
          }
          onChangeFare(e) {
            this.setState({
              fare: e.target.value
            });
          }
         
          onChangeBoardingTime(e) {
            this.setState({
              boardingtime: e.target.value
            });
          }
          onChangeArrivalTime(e) {
            this.setState({
              arrivaltime: e.target.value
            });
          }

          saveTutorial() {
            var data = {
              source: this.state.source,
              destination: this.state.destination,
              date:this.state.date,
              name:this.state.name,
              fare:this.state.fare,
              boardingtime:this.state.boardingtime,
              arrivaltime:this.state.arrivaltime

            };

            FlightService.create(data)
            .then(response => {
              this.setState({
                source: response.data.source,
                destination: response.data.destination,
                date: response.date.description,
                name: response.data.name,
                fare: response.data.fare,
                arrivaltime: response.data.arrivaltime,
                boardingtime: response.data.boardingtime,
      
                submitted: true
              });
              console.log(response.data);
            })
            .catch(e => {
              console.log(e);
            });
        }

        newFlight() {
            this.setState({
                source: "",
                destination: "",
                date: "", 
                name: "",
                fare:"",
                boardingtime:"",
                arrivaltime:"",
        
              submitted: false
            });
          }

    render() {
        return (
           <div className="submit-form">
        {this.state.submitted ? (
          <div>
            <h4>You submitted successfully!</h4>
            <button className="btn btn-success" onClick={this.newFlight}>
              Add
            </button>
          </div>
        ) : (
          <div>
            <div className="form-group">
              <label htmlFor="source">Source</label>
              <input
                type="text"
                className="form-control"
                id="source"
                required
                value={this.state.source}
                onChange={this.onChangeSource}
                name="souce"
              />
            </div>
            <div className="form-group">
              <label htmlFor="destination">Destination</label>
              <input
                type="text"
                className="form-control"
                id="destination"
                required
                value={this.state.destination}
                onChange={this.onChangeDestination}
                name="destination"
              />
            </div>
            <div className="form-group">
              <label htmlFor="date">Date</label>
              <input
                type="text"
                className="form-control"
                id="date"
                required
                value={this.state.date}
                onChange={this.onChangedate}
                name="date"
              />
            </div>
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input
                type="text"
                className="form-control"
                id="name"
                required
                value={this.state.name}
                onChange={this.onChangeName}
                name="name"
              />
            </div>
            <div className="form-group">
              <label htmlFor="fare">Fare</label>
              <input
                type="text"
                className="form-control"
                id="fare"
                required
                value={this.state.fare}
                onChange={this.onChangeFare}
                name="fare"
              />
            </div>
            <div className="form-group">
              <label htmlFor="boardingtime">Boardingtime</label>
              <input
                type="text"
                className="form-control"
                id="boardingtime"
                required
                value={this.state.boardingtime}
                onChange={this.onChangeBoardingTime}
                name="boardingtime"
              />
            </div>
            <div className="form-group">
              <label htmlFor="arrivaltime">Arrivaltime</label>
              <input
                type="text"
                className="form-control"
                id="arrivaltime"
                required
                value={this.state.arrivaltime}
                onChange={this.onChangeArrivalTime}
                name="arrivaltime"
              />
            </div>
            <button onClick={this.saveFlight} className="btn btn-success">
              Submit
            </button>
          </div>
        )}
      </div>
        )
    }
}

export default AddComponent
