import React, { Component } from "react";
import FlightsService from "../services/flight.service";
import BookService from "../services/book.service";
import AuthService from "../services/auth.service";
import "../book.css";


export default class Book extends Component {
  constructor(props) {
    super(props);
    this.getFlight = this.getFlight.bind(this);
    this.bookFlight = this.bookFlight.bind(this);
    this.onchangeQuantity = this.onchangeQuantity.bind(this);

    this.state = {
      currentUser: AuthService.getCurrentUser(),
      currentFlight: {
        id: null,
        flight_name: "",
        from: "",
        to: "",
        date: "",
        departuretime: "",
        arrivaltime: "",
        fare: "",
      },
      quantity: "1",
    };
  }

  onchangeQuantity(e) {
    this.setState({
      quantity: e.target.value,
    });
  }

  componentDidMount() {
    this.getFlight(this.props.match.params.id);
    console.log(this.state.currentUser);
    console.log(this.getFlight);
  }

  bookFlight(e) {
    e.preventDefault();
    console.log("inside bookflight");
    var currentFlight = {
      flight_name: this.state.currentFlight.flight_name,
      from: this.state.currentFlight.from,
      to: this.state.currentFlight.to,
      date: this.state.currentFlight.date,
      departuretime: this.state.currentFlight.departuretime,
      arrivaltime: this.state.currentFlight.arrivaltime,
      fare: this.state.currentFlight.fare,
    };
    console.log(currentFlight);
    BookService.BookFlight(
      this.state.currentUser,
      currentFlight,
      this.state.quantity
    )
      .then((response) => {
        console.log("inside booking");
        console.log(response);
      })
      .catch((e) => {
        console.log("inside error");
        console.log(e);
      });
  }

  getFlight(id) {
    FlightsService.get(id)
      .then((response) => {
        this.setState({
          currentFlight: response.data,
        });
        console.log(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }

  render() {
    // const { flight } = this.state;
    const { currentFlight, currentUser, bookingdata } = this.state;

    return (
      <div class="container">
        
       
          {currentFlight && currentUser ? (

            <div className="editform">
             
              <form class="form-style">
              <h3 class="text-white"> Booking Details</h3>
               
                 <div class="input">


                <div className="group">
                  <label class="text-white">
                    <strong>From</strong>
                  </label>
                  <input
                    type="text"
                    className="linecontrol"
                    value={currentFlight.from}
                    onChange={this.onChangeFrom}
                  />
                </div>

                 <div className="group">
                  <label class="text-white">
                    <strong>To</strong>
                  </label>
                  <input
                    type="text"
                    className="linecontrol"
                    value={currentFlight.to}
                    onChange={this.onChangeTo}
                  />
                </div>

                <span id="label" className="form-group">
                  <label>
                    <strong>Flight Name</strong>
                  </label>
                  <input
                    style={{ float: "left" }}
                    type="text"
                    className="control"
                    value={currentFlight.flight_name}
                    onChange={this.onChangeFlightName}
                  />
                </span>

                <span id="label" className="form-group">
                  <label htmlFor="description">
                    <strong>Date:</strong>
                  </label>
                  <input
                    style={{ marginLeft: "10px", float: "left" }}
                    type="text"
                    className="control"
                    id="description"
                    value={currentFlight.date}
                    onChange={this.onChangeDate}
                  />
                </span>

                <span id="label" className="form-group">
                  <label htmlFor="description">
                    <strong>Departure Time:</strong>
                  </label>
                  <input
                    style={{ marginLeft: "10px", float: "left" }}
                    type="text"
                    className="control"
                    id="description"
                    value={currentFlight.departuretime}
                    onChange={this.onChangeDepartureTime}
                  />
                </span>

                <span id="label" className="form-group">
                  <label htmlFor="description">
                    <strong>Arrival Time:</strong>
                  </label>
                  <input
                    style={{ marginLeft: "10px", float: "left" }}
                    type="text"
                    className="control"
                    id="description"
                    value={currentFlight.arrivaltime}
                    onChange={this.onChangeArrivalTime}
                  />
                </span>
                <span id="label" className="form-group">
                  <label htmlFor="description">
                    <strong>Fare:</strong>
                  </label>
                  <input
                    style={{ marginLeft: "10px", float: "left" }}
                    type="text"
                    className="control"
                    id="description"
                    value={currentFlight.fare}
                    onChange={this.onChangeFare}
                  />
                </span>

                <div className="">
                  <h4 style={{ marginLeft: "73px" }}>Traveller Details</h4>

                  <span id="" className="form-group">
                    <label>First Name</label>
                    <input
                      type="text"
                      className="control"
                      value={currentUser.username}
                      onChange={this.onChangeUsername}
                    />
                  </span>

                  <span id="" className="form-group">
                    <label>Last Name</label>
                    <input
                      type="text"
                      className="control"
                      value={currentUser.lastname}
                      onChange={this.onChangeTo}
                    />
                  </span>

                  <span id="" className="form-group">
                    <label>Gender</label>
                    <input
                      type="text"
                      className="control"
                      value={currentUser.gender}
                      onChange={this.onChangeDate}
                    />
                  </span>

                  <span id="" className="form-group">
                    <label>Quantity</label>
                    <input
                      type="text"
                      className="control"
                      value={this.state.quantity}
                      onChange={this.onchangeQuantity}
                    />
                  </span>
                  <div className="col text-center">
                    <button
                      onClick={(e) => {
                        this.bookFlight(e);
                      }}
                      id="button"
                      className="btn btn-default"
                    >
                      Continue
                    </button>
                  </div>
                  </div>
                </div>
              </form>
            </div>
          ) : (
            <div>
              <br />
              <p>Please Login First to continue..</p>
              <a id="loginlink" href="/login">
                Sign In
              </a>
            </div>
          )}
        
      </div>
    );
  }
}
