import React, { Component } from 'react'
import   UserSearchService from "../services/user_search.service";
import { Link } from 'react-router-dom';

export default class UserComponent extends Component {


    constructor(props) {
        super(props);
        
        this.onChangeFrom = this.onChangeFrom.bind(this);
        this.onChangeTo = this.onChangeTo.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.saveInfo = this.saveInfo.bind(this);
        
        
        
    
        this.state = {
          id: null,
          from: "",
          to: "",
          date: "",
          flights:[]
        };
      }
      
  onChangeFrom(e) {
    this.setState({
      from: e.target.value
    });
  }
  onChangeTo(e) {
    this.setState({
      to: e.target.value
    });
  }
  onChangeDate(e) {
    this.setState({
      date: e.target.value
    });
  }

  saveInfo() {
    var data = {
      from:this.state.from,
      to:this.state.to,
      date:this.state.date,
     };
    

     UserSearchService.findflights(data)
    .then(response => {
      this.setState({
        flights:response.data
      });
      console.log(response.data);
    })
    .catch(e => {
      console.log(e);
    });

}
 

   render() {
    const { flights } = this.state;
        return (
             <div>
            <div className="form-group">
              <label htmlFor="description"></label>
              <input
                type="text"
                className="form-control"
                id="description"
                required
                value={this.state.from}
                placeholder="From where?"
                onChange={this.onChangeFrom}
                name="description"
              />
            </div>

            <div className="form-group">
              <label htmlFor="description"></label>
              <input
                type="text"
                className="form-control"
                id="description"
                required
                value={this.state.to}
                placeholder="To where?"
                onChange={this.onChangeTo}
                name="description"
              />
            </div>

            < div className="form-group">
              <label htmlFor="description"></label>
              <input
                type="text"
                className="form-control"
                id="description"
                required
                value={this.state.date}
                placeholder="Date of Travel"
                onChange={this.onChangeDate}
                name="description"
              />
            </div>
            <button onClick={this.saveInfo} className="btn btn-info">
              Submit
            </button>
         
         
    
        
       
        <Table responsive>
        <thead>
        <tr>
        <th>#</th>
        <th>Source</th>
        <th>Destination</th>
        <th>Date</th>
        
        <th></th>
        <th></th>
        </tr>
        </thead>
        <tbody>
        {
        flights && flights.map((flights, i) => {
        return (
        <tr key={i}>
        <td>{i}</td>
        <td>{flights.from}</td>
        <td>{flights.to}</td>
        <td>{flights.date}</td>
        
        <td>
        <Link to={"/" + flights._id} className="btn btn-primary">Book</Link>
        </td>
        
        </tr>
          )
          })
          }
        </tbody>
        </Table>
          
          </div>
          
          
   )
           }
        
    
          }
